import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import { Line, Pie, Bar, Radar, Doughnut, Scatter, PolarArea, Bubble } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  RadarController,
  BarElement,
  Filler,
  RadialLinearScale,
  BubbleController,
  PolarAreaController,
  TimeScale,
} from 'chart.js';
import { CircleLoader } from 'react-spinners';
import './Dashboard.css';

// Register necessary ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  RadarController,
  BarElement,
  Filler,
  RadialLinearScale,
  BubbleController,
  PolarAreaController,
  TimeScale
);

function Dashboard() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [companyFilter, setCompanyFilter] = useState('All');
  const [engagementFilter, setEngagementFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });

  useEffect(() => {
    // Load CSV data
    Papa.parse('https://docs.google.com/spreadsheets/d/e/2PACX-1vSTTSyOvOYmKiGeKXUUzxGWjQ7z9QpoUv1rFM1kkz5g7_vgKNmbiOcZPBQnyQMQCLJcFTS_rJ03KVHD/pub?gid=0&single=true&output=csv', {
      download: true,
      header: true,
      dynamicTyping: true,
      complete: (result) => {
        setData(result.data);
        setLoading(false);
      },
    });

    return () => {
      const canvas = document.getElementsByTagName('canvas');
      if (canvas.length > 0) {
        const chart = ChartJS.getChart(canvas[0]);
        if (chart) {
          chart.destroy();
        }
      }
    };
  }, []);

  // Filter data by company, engagement, and date range
  const filteredData = data
    .filter(entry => companyFilter === 'All' || entry['Company Name'] === companyFilter)
    .filter(entry => engagementFilter === '' || entry['Engagement (%)'] >= engagementFilter)
    .filter(entry => {
      const entryDate = new Date(entry['Date']);
      const startDate = new Date(dateRange.start);
      const endDate = new Date(dateRange.end);
      return (!dateRange.start || entryDate >= startDate) && (!dateRange.end || entryDate <= endDate);
    });

  // Gather necessary metrics
  const engagementData = filteredData.map((entry) => entry['Engagement (%)']);
  const reachData = filteredData.map((entry) => entry['Reach']);
  const impressionsData = filteredData.map((entry) => entry['Impressions']);
  const followersData = filteredData.map((entry) => entry['Followers Gained']);
  const dates = filteredData.map((entry) => entry['Date']);
  const platforms = ['Instagram', 'Facebook', 'Twitter', 'LinkedIn'];
  const engagementByPlatform = platforms.map((platform) => Math.floor(Math.random() * 100));

  // Prepare data for the charts
  const engagementChartData = {
    labels: dates,
    datasets: [
      {
        label: 'Engagement Rate (%)',
        data: engagementData,
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
        tension: 0.1,
      },
    ],
  };

  const barChartData = {
    labels: dates,
    datasets: [
      {
        label: 'Daily Engagement',
        data: engagementData,
        backgroundColor: 'rgba(255,99,132,0.2)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
      },
    ],
  };

  const doughnutChartData = {
    labels: platforms,
    datasets: [
      {
        data: engagementByPlatform,
        backgroundColor: ['rgba(75,192,192,0.5)', 'rgba(153,102,255,0.5)', 'rgba(54,162,235,0.5)', 'rgba(255,159,64,0.5)'],
        borderColor: ['rgba(75,192,192,1)', 'rgba(153,102,255,1)', 'rgba(54,162,235,1)', 'rgba(255,159,64,1)'],
        borderWidth: 1,
      },
    ],
  };

  const radarChartData = {
    labels: ['Reach', 'Impressions', 'Engagement', 'Followers Gained'],
    datasets: [
      {
        label: 'Monthly Performance',
        data: [
          reachData.reduce((acc, curr) => acc + curr, 0),
          impressionsData.reduce((acc, curr) => acc + curr, 0),
          engagementData.reduce((acc, curr) => acc + curr, 0),
          followersData.reduce((acc, curr) => acc + curr, 0),
        ],
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  const scatterChartData = {
    datasets: [
      {
        label: 'Followers vs Impressions',
        data: followersData.map((followers, index) => ({
          x: followers,
          y: impressionsData[index],
        })),
        backgroundColor: 'rgba(255,99,132,0.6)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
      },
    ],
  };

  // New metrics (charts) to add
  const pieChartData = {
    labels: platforms,
    datasets: [
      {
        data: engagementByPlatform,
        backgroundColor: ['#FF5733', '#33FF57', '#3357FF', '#FF33B5'],
        borderColor: ['#FF5733', '#33FF57', '#3357FF', '#FF33B5'],
        borderWidth: 1,
      },
    ],
  };

  const bubbleChartData = {
    datasets: [
      {
        label: 'Engagement vs Reach vs Impressions',
        data: followersData.map((followers, index) => ({
          x: reachData[index],
          y: impressionsData[index],
          r: engagementData[index] * 2, // Bubble size based on engagement
        })),
        backgroundColor: 'rgba(255,159,64,0.6)',
        borderColor: 'rgba(255,159,64,1)',
        borderWidth: 1,
      },
    ],
  };

  const lineChartData = {
    labels: dates,
    datasets: [
      {
        label: 'Engagement Trend Over Time',
        data: engagementData,
        borderColor: 'rgba(153,102,255,1)',
        fill: false,
        tension: 0.1,
      },
    ],
  };

  const reachVsImpressionsData = {
    labels: companyList,
    datasets: [
      {
        label: 'Reach vs Impressions',
        data: companyList.map(company => {
          const companyData = filteredData.filter(entry => entry['Company Name'] === company);
          return {
            reach: companyData.reduce((acc, curr) => acc + curr['Reach'], 0),
            impressions: companyData.reduce((acc, curr) => acc + curr['Impressions'], 0),
          };
        }),
        backgroundColor: 'rgba(75,192,192,0.5)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  const companyList = ['All', ...new Set(data.map(entry => entry['Company Name']))]; // List of unique company names
  const engagementLevels = [0, 20, 40, 60, 80, 100];

  if (loading) {
    return (
      <div className="loading-container">
        <CircleLoader color="#00BFFF" size={80} />
        <p>Loading data...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="sidebar">
        <h3>Dashboard</h3>
        <select onChange={(e) => setCompanyFilter(e.target.value)} value={companyFilter}>
          {companyList.map((company, index) => (
            <option key={index} value={company}>{company}</option>
          ))}
        </select>

        <select onChange={(e) => setEngagementFilter(e.target.value)} value={engagementFilter}>
          <option value="">Select Engagement Filter</option>
          {engagementLevels.map((level) => (
            <option key={level} value={level}>{`> ${level}%`}</option>
          ))}
        </select>

        <div className="date-range-picker">
          <input
            type="date"
            value={dateRange.start}
            onChange={(e) => setDateRange({ ...dateRange, start: e.target.value })}
          />
          <input
            type="date"
            value={dateRange.end}
            onChange={(e) => setDateRange({ ...dateRange, end: e.target.value })}
          />
        </div>
      </div>

      <div className="main-content">
        <h2>Social Media Performance Dashboard</h2>
        <div className="chart-container">
          <div className="chart-card">
            <h3>Engagement Rate Over Time</h3>
            <Line data={engagementChartData} />
          </div>

          <div className="chart-card">
            <h3>Daily Engagement Comparison</h3>
            <Bar data={barChartData} />
          </div>

          <div className="chart-card">
            <h3>Engagement by Platform</h3>
            <Doughnut data={doughnutChartData} />
          </div>

          <div className="chart-card">
            <h3>Monthly Performance</h3>
            <Radar data={radarChartData} />
          </div>

          <div className="chart-card">
            <h3>Followers vs Impressions</h3>
            <Scatter data={scatterChartData} />
          </div>

          {/* New charts */}
          <div className="chart-card">
            <h3>Engagement by Platform (Pie)</h3>
            <Pie data={pieChartData} />
          </div>

          <div className="chart-card">
            <h3>Engagement vs Reach vs Impressions (Bubble)</h3>
            <Bubble data={bubbleChartData} />
          </div>

          <div className="chart-card">
            <h3>Engagement Trend Over Time (Line)</h3>
            <Line data={lineChartData} />
          </div>

          <div className="chart-card">
            <h3>Reach vs Impressions by Company</h3>
            <Bar data={reachVsImpressionsData} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
