// Dashboard.js
import React, { useEffect, useState } from 'react';
import Papa from 'papaparse';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  const sheetLink = localStorage.getItem('sheetLink');
  const clientName = localStorage.getItem('clientName');
  const logoURL = localStorage.getItem('logoURL');

  useEffect(() => {
    if (!localStorage.getItem('isLoggedIn')) {
      navigate('/');
    }

    fetch(sheetLink)
      .then(response => response.text())
      .then(csv => {
        Papa.parse(csv, {
          header: true,
          skipEmptyLines: true,
          complete: results => {
            setData(results.data);
          }
        });
      });
  }, [sheetLink, navigate]);

  return (
    <div style={{ padding: '20px' }}>
      {/* Client branding section */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '2rem' }}>
        <img src={logoURL} alt="Client Logo" style={{ height: '60px', marginRight: '20px' }} />
        <h2>{clientName} Dashboard</h2>
      </div>

      {/* Table data from Google Sheet */}
      <h3>Campaign Metrics</h3>
      <table border="1" cellPadding="10" cellSpacing="0">
        <thead>
          <tr>
            {data.length > 0 &&
              Object.keys(data[0]).map((key, index) => (
                <th key={index}>{key}</th>
              ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {Object.values(row).map((value, colIndex) => (
                <td key={colIndex}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
