import React, { useState } from 'react';
import {
  Line, Bar, Doughnut, Radar, PolarArea,
} from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  RadialLinearScale,
  Tooltip,
  Legend,
  Filler,
  Title,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  RadialLinearScale,
  Tooltip,
  Legend,
  Filler,
  Title
);

function Dashboard() {
  const [showInsights, setShowInsights] = useState({
    lineChart: false,
    areaChart: false,
    barChart: false,
    doughnutChart: false,
    radarChart: false,
    polarChart: false,
    stackedBar: false,
  });

  const updateToggle = (chart) => {
    setShowInsights((prev) => ({ ...prev, [chart]: !prev[chart] }));
  };

  const dates = ['2025-04-01', '2025-04-02', '2025-04-03', '2025-04-04'];
  const engagementData = [100, 150, 90, 200];
  const impressionsData = [300, 450, 200, 500];
  const reachData = [250, 400, 180, 450];
  const platforms = ['Instagram', 'YouTube', 'TikTok', 'Facebook'];
  const engagementByPlatform = [40, 30, 20, 10];
  const followersByPlatform = [200, 300, 100, 150];

  return (
    <div className="dashboard">
      <h2>Analytics Dashboard</h2>
      <div className="charts-container" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '2rem' }}>

        {/* Line Chart */}
        <div className="chart-card">
          <h3>Engagement Over Time</h3>
          <Line
            data={{
              labels: dates,
              datasets: [{
                label: 'Engagement',
                data: engagementData,
                fill: false,
                borderColor: 'rgb(75, 192, 192)',
              }]
            }}
          />
          <button onClick={() => updateToggle('lineChart')}>Toggle Insight</button>
          {showInsights.lineChart && (
            <p><em>Line chart shows how engagement changed over time.</em></p>
          )}
        </div>

        {/* Area Chart */}
        <div className="chart-card">
          <h3>Engagement (Area Chart)</h3>
          <Line
            data={{
              labels: dates,
              datasets: [{
                label: 'Engagement',
                data: engagementData,
                fill: true,
                backgroundColor: 'rgba(75,192,192,0.2)',
                borderColor: 'rgba(75,192,192,1)',
              }]
            }}
          />
          <button onClick={() => updateToggle('areaChart')}>Toggle Insight</button>
          {showInsights.areaChart && (
            <p><em>Area chart emphasizes volume of engagement over time.</em></p>
          )}
        </div>

        {/* Bar Chart */}
        <div className="chart-card">
          <h3>Daily Engagement</h3>
          <Bar
            data={{
              labels: dates,
              datasets: [{
                label: 'Engagement',
                data: engagementData,
                backgroundColor: 'rgba(153,102,255,0.6)',
              }]
            }}
          />
          <button onClick={() => updateToggle('barChart')}>Toggle Insight</button>
          {showInsights.barChart && (
            <p><em>Bar chart helps compare engagement for each day clearly.</em></p>
          )}
        </div>

        {/* Doughnut Chart */}
        <div className="chart-card">
          <h3>Engagement by Platform</h3>
          <Doughnut
            data={{
              labels: platforms,
              datasets: [{
                label: 'Engagement %',
                data: engagementByPlatform,
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
              }]
            }}
          />
          <button onClick={() => updateToggle('doughnutChart')}>Toggle Insight</button>
          {showInsights.doughnutChart && (
            <p><em>Doughnut chart shows share of engagement by platform.</em></p>
          )}
        </div>

        {/* Radar Chart */}
        <div className="chart-card">
          <h3>Followers Gained by Platform (Radar)</h3>
          <Radar
            data={{
              labels: platforms,
              datasets: [{
                label: 'Followers Gained',
                data: followersByPlatform,
                backgroundColor: 'rgba(179,181,198,0.2)',
                borderColor: 'rgba(179,181,198,1)',
                pointBackgroundColor: 'rgba(179,181,198,1)',
              }]
            }}
          />
          <button onClick={() => updateToggle('radarChart')}>Toggle Insight</button>
          {showInsights.radarChart && (
            <p><em>Radar chart compares follower growth across platforms.</em></p>
          )}
        </div>

        {/* Polar Area Chart */}
        <div className="chart-card">
          <h3>Platform Distribution - Polar Area</h3>
          <PolarArea
            data={{
              labels: platforms,
              datasets: [{
                label: 'Followers Gained',
                data: followersByPlatform,
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0'],
              }]
            }}
          />
          <button onClick={() => updateToggle('polarChart')}>Toggle Insight</button>
          {showInsights.polarChart && (
            <p><em>Polar chart shows magnitude of followers per platform using radial slices.</em></p>
          )}
        </div>

        {/* Stacked Bar Chart */}
        <div className="chart-card">
          <h3>Stacked Bar - Impressions & Reach</h3>
          <Bar
            data={{
              labels: dates,
              datasets: [
                {
                  label: 'Impressions',
                  data: impressionsData,
                  backgroundColor: 'rgba(54,162,235,0.6)',
                },
                {
                  label: 'Reach',
                  data: reachData,
                  backgroundColor: 'rgba(255,206,86,0.6)',
                },
              ],
            }}
            options={{
              responsive: true,
              plugins: {
                legend: { position: 'top' },
              },
              scales: {
                x: { stacked: true },
                y: { stacked: true },
              },
            }}
          />
          <button onClick={() => updateToggle('stackedBar')}>Toggle Insight</button>
          {showInsights.stackedBar && (
            <p><em>Stacked bar chart helps compare total and relative performance of impressions vs reach per day.</em></p>
          )}
        </div>

      </div>
    </div>
  );
}

export default Dashboard;
