import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import { Line, Pie, Bar, Radar, Doughnut, Scatter, PolarArea, Bubble } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  RadarController,
  BarElement,
  Filler,
  RadialLinearScale,
  BubbleController,
  PolarAreaController,
  TimeScale,
} from 'chart.js';
import { CircleLoader } from 'react-spinners';
import './Dashboard.css';

// Register necessary ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  RadarController,
  BarElement,
  Filler,
  RadialLinearScale,
  BubbleController,
  PolarAreaController,
  TimeScale
);

function Dashboard() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedPlatform, setSelectedPlatform] = useState('All');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });

  useEffect(() => {
    // Load CSV data
    Papa.parse('https://docs.google.com/spreadsheets/d/e/2PACX-1vSTTSyOvOYmKiGeKXUUzxGWjQ7z9QpoUv1rFM1kkz5g7_vgKNmbiOcZPBQnyQMQCLJcFTS_rJ03KVHD/pub?gid=0&single=true&output=csv', {
      download: true,
      header: true,
      dynamicTyping: true,
      complete: (result) => {
        setData(result.data);
        setLoading(false);
      },
    });

    return () => {
      const canvas = document.getElementsByTagName('canvas');
      if (canvas.length > 0) {
        const chart = ChartJS.getChart(canvas[0]);
        if (chart) {
          chart.destroy();
        }
      }
    };
  }, []);

  const platforms = ['All', 'Instagram', 'Facebook', 'Twitter', 'LinkedIn'];

  // Filter data based on platform and date range
  const filteredData = data.filter((entry) => {
    const date = new Date(entry['Date']);
    const withinDateRange = (!dateRange.start || date >= new Date(dateRange.start)) && 
                            (!dateRange.end || date <= new Date(dateRange.end));

    const matchesPlatform = selectedPlatform === 'All' || entry['Platform'] === selectedPlatform;

    return withinDateRange && matchesPlatform;
  });

  // Extract metrics from filtered data
  const engagementData = filteredData.map((entry) => entry['Engagement (%)']);
  const reachData = filteredData.map((entry) => entry['Reach']);
  const impressionsData = filteredData.map((entry) => entry['Impressions']);
  const followersData = filteredData.map((entry) => entry['Followers Gained']);
  const dates = filteredData.map((entry) => entry['Date']);
  const engagementByPlatform = platforms.map(() => Math.floor(Math.random() * 100));

  // Line chart for Engagement Rate vs Time
  const engagementChartData = {
    labels: dates,
    datasets: [
      {
        label: 'Engagement Rate (%)',
        data: engagementData,
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
        tension: 0.1,
      },
    ],
  };

  // Bar chart for Daily Engagement Comparison
  const barChartData = {
    labels: dates,
    datasets: [
      {
        label: 'Daily Engagement',
        data: engagementData,
        backgroundColor: 'rgba(255,99,132,0.2)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
      },
    ],
  };

  // Doughnut chart for Engagement by Platform
  const doughnutChartData = {
    labels: platforms,
    datasets: [
      {
        data: engagementByPlatform,
        backgroundColor: ['rgba(75,192,192,0.5)', 'rgba(153,102,255,0.5)', 'rgba(54,162,235,0.5)', 'rgba(255,159,64,0.5)'],
        borderColor: ['rgba(75,192,192,1)', 'rgba(153,102,255,1)', 'rgba(54,162,235,1)', 'rgba(255,159,64,1)'],
        borderWidth: 1,
      },
    ],
  };

  // Radar chart for Monthly Performance Comparison
  const radarChartData = {
    labels: ['Reach', 'Impressions', 'Engagement', 'Followers Gained'],
    datasets: [
      {
        label: 'Monthly Performance',
        data: [
          reachData.reduce((acc, curr) => acc + curr, 0),
          impressionsData.reduce((acc, curr) => acc + curr, 0),
          engagementData.reduce((acc, curr) => acc + curr, 0),
          followersData.reduce((acc, curr) => acc + curr, 0),
        ],
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  // Scatter chart for Followers vs Impressions
  const scatterChartData = {
    datasets: [
      {
        label: 'Followers vs Impressions',
        data: followersData.map((followers, index) => ({
          x: followers,
          y: impressionsData[index],
        })),
        backgroundColor: 'rgba(255,99,132,0.6)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
      },
    ],
  };

  // Mock data for Bubble chart
  const bubbleChartData = {
    datasets: [
      {
        label: 'Engagement vs Impressions',
        data: followersData.map((followers, index) => ({
          x: followers,
          y: impressionsData[index],
          r: 10,  // Size of the bubble
        })),
        backgroundColor: 'rgba(255,99,132,0.6)',
        borderColor: 'rgba(255,99,132,1)',
        borderWidth: 1,
      },
    ],
  };

  // Mock data for Polar Area chart
  const polarAreaChartData = {
    labels: platforms,
    datasets: [
      {
        data: engagementByPlatform,
        backgroundColor: ['rgba(75,192,192,0.5)', 'rgba(153,102,255,0.5)', 'rgba(54,162,235,0.5)', 'rgba(255,159,64,0.5)'],
        borderColor: ['rgba(75,192,192,1)', 'rgba(153,102,255,1)', 'rgba(54,162,235,1)', 'rgba(255,159,64,1)'],
        borderWidth: 1,
      },
    ],
  };

  if (loading) {
    return (
      <div className="loading-container">
        <CircleLoader color="#00BFFF" size={80} />
        <p>Loading data...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <h2>Social Media Performance Dashboard</h2>

      {/* Filter Section */}
      <div className="filters">
        <div>
          <label>Platform: </label>
          <select onChange={(e) => setSelectedPlatform(e.target.value)} value={selectedPlatform}>
            {platforms.map((platform) => (
              <option key={platform} value={platform}>
                {platform}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Start Date: </label>
          <input
            type="date"
            value={dateRange.start}
            onChange={(e) => setDateRange({ ...dateRange, start: e.target.value })}
          />
        </div>

        <div>
          <label>End Date: </label>
          <input
            type="date"
            value={dateRange.end}
            onChange={(e) => setDateRange({ ...dateRange, end: e.target.value })}
          />
        </div>
      </div>

      {/* Chart Section */}
      <div className="chart-container">
        {/* Existing Charts */}
        <div className="chart-card">
          <h3>Engagement Rate Over Time</h3>
          <Line data={engagementChartData} />
        </div>

        <div className="chart-card">
          <h3>Daily Engagement Comparison</h3>
          <Bar data={barChartData} />
        </div>

        <div className="chart-card">
          <h3>Engagement by Platform</h3>
          <Doughnut data={doughnutChartData} />
        </div>

        <div className="chart-card">
          <h3>Monthly Performance</h3>
          <Radar data={radarChartData} />
        </div>

        <div className="chart-card">
          <h3>Followers vs Impressions</h3>
          <Scatter data={scatterChartData} />
        </div>

        {/* New Charts */}
        <div className="chart-card">
          <h3>Engagement vs Impressions (Bubble Chart)</h3>
          <Bubble data={bubbleChartData} />
        </div>

        <div className="chart-card">
          <h3>Platform-Based Performance (Polar Area Chart)</h3>
          <PolarArea data={polarAreaChartData} />
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
