import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import { Line, Pie, Bar, Radar, Doughnut, PolarArea } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  RadarController,
  BarElement,
  Filler,
  RadialLinearScale,
  PolarAreaController,
  TimeScale,
} from 'chart.js';
import { CircleLoader } from 'react-spinners';
import './Dashboard.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  RadarController,
  BarElement,
  Filler,
  RadialLinearScale,
  PolarAreaController,
  TimeScale
);

const calculatePercentageChange = (data) => {
  if (data.length < 2) return 'Not enough data';
  const first = data[0];
  const last = data[data.length - 1];
  const change = ((last - first) / Math.abs(first)) * 100;
  return change.toFixed(2);
};

const calculateAverage = (data) => {
  if (data.length === 0) return 0;
  const sum = data.reduce((a, b) => a + b, 0);
  return (sum / data.length).toFixed(2);
};

function Dashboard() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [companyFilter, setCompanyFilter] = useState('All');
  const [engagementFilter, setEngagementFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [darkMode, setDarkMode] = useState(false);

  const [showInsights, setShowInsights] = useState({
    engagementLine: false,
    areaChart: false,
    barChart: false,
    doughnutChart: false,
    radarChart: false,
    polarChart: false,
    stackedBar: false,
  });

  useEffect(() => {
    Papa.parse('/data.csv', {
      download: true,
      header: true,
      dynamicTyping: true,
      complete: (result) => {
        setData(result.data);
        setLoading(false);
      },
    });
  }, []);

  const filteredData = data
    .filter(entry => companyFilter === 'All' || entry['Company Name'] === companyFilter)
    .filter(entry => engagementFilter === '' || entry['Engagement (%)'] >= engagementFilter)
    .filter(entry => {
      const entryDate = new Date(entry['Date']);
      const startDate = new Date(dateRange.start);
      const endDate = new Date(dateRange.end);
      return (!dateRange.start || entryDate >= startDate) && (!dateRange.end || entryDate <= endDate);
    });

  const engagementData = filteredData.map(entry => entry['Engagement (%)']);
  const reachData = filteredData.map(entry => entry['Reach']);
  const impressionsData = filteredData.map(entry => entry['Impressions']);
  const followersData = filteredData.map(entry => entry['Followers Gained']);
  const dates = filteredData.map(entry => entry['Date']);
  const platforms = ['Instagram', 'Facebook', 'Twitter', 'LinkedIn', 'YouTube'];
  const engagementByPlatform = platforms.map(platform => {
    const platformData = filteredData.filter(entry => entry['Platform'] === platform);
    return calculateAverage(platformData.map(e => e['Engagement (%)']));
  });

  const chartToggles = Object.keys(showInsights);

  const updateToggle = (key) => {
    setShowInsights(prev => ({ ...prev, [key]: !prev[key] }));
  };

  return (
    <div className={`dashboard ${darkMode ? 'dark' : ''}`}>
      {loading ? (
        <CircleLoader color="#36d7b7" loading={loading} size={150} />
      ) : (
        <div>
          <h1>Social Media Analytics Dashboard</h1>
          <div className="filters">
            {/* Filters go here */}
          </div>

          <div className="chart-section">
            {/* Example Line Chart for Engagement */}
            <Line
              data={{
                labels: dates,
                datasets: [
                  {
                    label: 'Engagement (%) Over Time',
                    data: engagementData,
                    borderColor: 'rgba(75,192,192,1)',
                    fill: false,
                  },
                ],
              }}
            />

            {/* Doughnut Chart: Engagement by Platform */}
            <Doughnut
              data={{
                labels: platforms,
                datasets: [
                  {
                    data: engagementByPlatform,
                    backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
                  },
                ],
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
