import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const users = [
    {
      email: 'client1@example.com',
      password: '123456',
      sheet: 'https://docs.google.com/spreadsheets/d/1A16kwTfWmGsO0Z7UmmsqQAnR2gtfJXTQk0OwTLS83sk/export?format=csv',
      clientName: 'Client One',
      logoURL: 'https://upload.wikimedia.org/wikipedia/commons/a/a7/React-icon.svg'
    },
    {
      email: 'client2@example.com',
      password: 'abcdef',
      sheet: 'https://docs.google.com/spreadsheets/d/YOUR_OTHER_SHEET_ID/export?format=csv',
      clientName: 'Client Two',
      logoURL: 'https://upload.wikimedia.org/wikipedia/commons/4/4f/Iconic_logo_sample.png'
    }
  ];

  const handleLogin = () => {
    const user = users.find(u => u.email === email && u.password === password);
    if (user) {
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('sheetLink', user.sheet);
      localStorage.setItem('clientName', user.clientName);
      localStorage.setItem('logoURL', user.logoURL);
      navigate('/dashboard');
    } else {
      setError('Invalid email or password.');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>Login</h2>

        {error && <p style={styles.error}>{error}</p>}

        <input
          type="text"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          style={styles.input}
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          style={styles.input}
        />

        <button onClick={handleLogin} style={styles.button}>
          Login
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    display: 'flex',
    padding: '20px',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(to bottom right, #e0f7fa, #e1bee7)',
  },
  card: {
    background: '#fff',
    borderRadius: '12px',
    padding: '30px 20px',
    width: '100%',
    maxWidth: '400px',
    boxShadow: '0 4px 16px rgba(0, 0, 0, 0.1)',
    display: 'flex',
    flexDirection: 'column',
    gap: '16px',
  },
  title: {
    margin: 0,
    textAlign: 'center',
    fontSize: '24px',
    color: '#673ab7',
  },
  input: {
    padding: '12px',
    fontSize: '16px',
    borderRadius: '8px',
    border: '1px solid #ccc',
    outline: 'none',
  },
  button: {
    padding: '12px',
    fontSize: '16px',
    borderRadius: '8px',
    background: '#673ab7',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    transition: 'background 0.3s',
  },
  error: {
    color: 'red',
    fontSize: '14px',
    textAlign: 'center',
    marginTop: '-8px',
    marginBottom: '-4px',
  },
};

export default Login;
